function stripTags(s, n) {
    return s.replace(/<.*?>/ig, '').split(/\s+/).slice(0, n - 1).join(' ')
}

rn = "<h5>No related post available</h5>";
rcomment = "comments";
rdisable = "disable comments";
commentYN = "no";
var dw = "<h3 class='related-post-title'>Čekni aj toto:</h3>";
titles = new Array;
titlesNum = 0;
urls = new Array;
timeR = new Array;
thumb = new Array;
commentsNum = new Array;
comments = new Array;
ignoreThis = new Array;

function currently_displayed_article() {
        // Return one displayed article's URL from its title
        try {
         return document.getElementsByClassName('timestamp-link')[0].getAttribute('name');
        } catch(err) {
            return "";
        }

}

function related_results_labels(c) {
    for (var b = 0; b < c.feed.entry.length; b++) {
        var d = c.feed.entry[b];
        titles[titlesNum] = d.title.$t;
        for (var a = 0; a < d.link.length; a++) {
            if ("thr$total" in d) commentsNum[titlesNum] = d.thr$total.$t + " " + rcomment;
            else commentsNum[titlesNum] = rdisable;
            if (d.link[a].rel == "alternate") {
                                if (d.link[a].href == currently_displayed_article()){
                                        ignoreThis[titlesNum] = true;
                                } else {
                                        ignoreThis[titlesNum] = false;
                                }

                urls[titlesNum] = d.link[a].href;
                timeR[titlesNum] = d.published.$t;
                                // if postText starts with <img>, find can't find it...
                var postText = d.content.$t;

                                var imgUrlPos = postText.indexOf("<img "); //$(postText).find("img:first").attr("src") // -- this loads all the large images from the other blog, so just use str and regex
                                console.log(imgUrlPos);
                                var imgUrl;
                                if (imgUrlPos > -1) {
                                        // get the first href after the position of the img src
                                        var postSubText = postText.substring(imgUrlPos);
                                        imgUrl = postSubText.match(/http[s]?:\/\/[^"]*\.[jpegnif]{3,4}" /i);
                                }

                if (imgUrl) thumb[titlesNum] = imgUrl[0].substring(0, imgUrl[0].length-2);
                else thumb[titlesNum] = "http://lh3.ggpht.com/--Z8SVBQZ4X8/TdDxPVMl_sI/AAAAAAAAAAA/jhAgjCpZtRQ/no-image.png";
                titlesNum++;
                break;
            }
        }
    }
}

function removeRelatedDuplicates() {
    var b = new Array(0);
    c = new Array(0);
    e = new Array(0);
    f = new Array(0);
    g = new Array(0);
    for (var a = 0; a < urls.length; a++)
        if (!contains(b, urls[a])
                        && !ignoreThis[a]) {
            b.length += 1;
            b[b.length - 1] = urls[a];
            c.length += 1;
            c[c.length - 1] = titles[a];
            e.length += 1;
            e[e.length - 1] = timeR[a];
            f.length += 1;
            f[f.length - 1] = thumb[a];
            g.length += 1;
            g[g.length - 1] = commentsNum[a];
        }
    urls = b;
    titles = c;
    timeR = e;
    thumb = f;
    commentsNum = g;
}

function contains(b, d) {
    for (var c = 0; c < b.length; c++)
        if (b[c] == d) return true;
    return false
}

related_displayed = false;

function printRelatedLabels(a) {
        if (!related_displayed){
                var y = a.indexOf("?m=0");
                if (y != -1) a = a.replace(/\?m=0/g, "");
                for (var b = 0; b < urls.length; b++)
                        if (urls[b] == a) {
                                urls.splice(b, 1);
                                titles.splice(b, 1);
                                timeR.splice(b, 1);
                                thumb.splice(b, 1);
                                commentsNum.splice(b, 1)
                        }

                var b = 0;
                var no_of_related = 6
                if (titles.length == 0) {
                        dw += rn;
                } else {
                        while (b < titles.length && b < no_of_related) {
                                if (y != -1) urls[b] = urls[b] + "?m=0";
                                if (commentYN == "yes") comments[b] = " - " + commentsNum[b];
                                else comments[b] = "";
                                dw += '<div class="mod-entry"><a href="' + urls[b] + '"><div class="post-imgs" style="background: url(\'' + thumb[b] + '\'); background-size: cover; background-position: center top"><span class="text-content"><span>' + titles[b] + '</span></span></div></a></div>'
                                b++
                        }
                }
                urls.splice(0, urls.length);
                titles.splice(0, titles.length);
                document.getElementById("related-posts").innerHTML = dw
                related_displayed = true;
        };
};

